# -*- coding: utf-8 -*-
from .base import BaseCache


class NullCache(BaseCache):

    def __init__(self):
        pass

    def add(self, key, value, timeout=None):
        return True

    def clear(self):
        pass

    def dec(self, key, delta=1, timeout=None):
        return True

    def delete(self, key):
        return True

    def delete_many(self, *keys):
        return [True for _ in keys]

    def get(self, key, **options):
        return None

    def get_many(self, *keys, **kwargs):
        return [None for _ in keys]

    def inc(self, key, delta=1, timeout=None):
        return True

    def set(self, key, value, timeout=None, **options):
        return True

    def set_many(self, mapping, timeout=None, **options):
        return {k: True for k in mapping}

    def exists(self, key):
        return False

    def reset(self):
        """
        disconnect all connections
        """
        pass
